mapper.select:
SELECT DISTINCT RES.ID_, RES.NAME_, RES.ASSIGNEE_, D.NAME_ AS PROC_NAME_, D.KEY_ AS PROC_KEY_, E.BUSINESS_KEY_ FROM ACT_RU_TASK RES LEFT JOIN ACT_RU_IDENTITYLINK I ON I.TASK_ID_ = RES.ID_ INNER JOIN ACT_RE_PROCDEF D ON RES.PROC_DEF_ID_ = D.ID_ INNER JOIN ACT_RU_EXECUTION E ON RES.PROC_INST_ID_ = E.ID_
#{key}
and E.START_TIME_ between #{startTime} and #{endTime}
and E.START_TIME_ >= #{startTime}
and E.START_TIME_ <= #{endTime}
AND I.TYPE_ = 'starter' AND I.USER_ID_ = #{startUserId}
AND RES.ASSIGNEE_ = #{userId} OR ( RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'candidate' AND ( I.USER_ID_ = #{userId} )) OR ( RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'notify' AND ( I.USER_ID_ = #{userId} ))

SELECT DISTINCT count( DISTINCT RES.ID_) FROM ACT_RU_TASK RES LEFT JOIN ACT_RU_IDENTITYLINK I ON I.TASK_ID_ = RES.ID_ INNER JOIN ACT_RE_PROCDEF D ON RES.PROC_DEF_ID_ = D.ID_ INNER JOIN ACT_RU_EXECUTION E ON RES.PROC_INST_ID_ = E.ID_
#{key}
and RES.START_TIME_ between #{startTime} and #{endTime}
and RES.START_TIME_ >= #{startTime}
and RES.START_TIME_ <= #{endTime}
AND I.TYPE_ = 'starter' AND I.USER_ID_ = #{startUserId}
AND RES.ASSIGNEE_ = #{userId} OR ( RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'candidate' AND ( I.USER_ID_ = #{userId} )) OR ( RES.ASSIGNEE_ IS NULL AND I.TYPE_ = 'notify' AND ( I.USER_ID_ = #{userId} ))
